pageContext.rootQuery:
{
  prismic {
    page(lang: "en-gb", uid: "submissions") {
      quote
      page_text
      title
      _meta {
        uid
      }
      body1 {
        ... on PRISMIC_PageBody1Info_card {
          type
          fields {
            card_text
          }
        }
        ... on PRISMIC_PageBody1Past_projects {
          type
          label
          primary {
            past_projects_title
          }
          fields {
            project_date
            project_description
            researcher_name
            video_url
          }
        }
      }
      image
    }
  }
}
