pageContext.rootQuery:
{
  prismic {
    page(lang: "en-gb", uid: "home") {
      quote
      body1 {
        ... on PRISMIC_PageBody1Info_card {
          type
          fields {
            card_header
            card_text
          }
        }
        ... on PRISMIC_PageBody1Past_projects {
          type
          label
          primary {
            past_projects_title
          }
          fields {
            project_date
            project_description
            researcher_name
            video_url
          }
        }
        ... on PRISMIC_PageBody1Large_info_card {
          primary {
            large_card_title
            large_card_section_intro
          }
          fields {
            card_title
            large_card_text
            card_subheader
          }
        }
      }
      _meta {
        uid
      }
      page_text
    }
  }
}
